import React from "react";
import { ShoppingOutlined, ShoppingFilled } from "@ant-design/icons";
import { useNavigate } from "react-router-dom";
import { Image, Tooltip } from "antd";
import { useDispatch, useSelector } from "react-redux";

import { addToCart } from "app/slices/cartSlice";

function ProductCard({ product }) {
  const navigate = useNavigate();
  const { language } = useSelector((state) => state.auth);
  const dispatch = useDispatch();
  const handleOnclickProduct = () => {
    if (product?.categoryId === 1) {
      navigate(`/products/foods/${product?.name}`);
    } else if (product?.categoryId === 2) {
      navigate(`/products/accessories/${product?.name}`);
    } else navigate(`/products/pets/${product?.name}`);
  };

  const handleAddToCart = (e) => {
    e.stopPropagation();

    dispatch(
      addToCart({
        ...product,
        orderQuantity: 1,
      })
    );
  };
  return (
    <div
      className='product-card group w-full bg-[#eee] text-center rounded-lg border-2 border-dashed shadow-sm cursor-pointer'
      onClick={handleOnclickProduct}
    >
      <div className='w-full overflow-hidden relative'>
        <Image
          className='product-img h-[285px]'
          src={product?.images[0]?.url}
          preview={false}
          fallback='data:image/png;base64,'
        />
        <div className='product-card__card-icon absolute bottom-5 left-5 text-3xl text-primary hover:cursor-pointer'>
          <ShoppingOutlined className='icon-outline fly-in' />
          <Tooltip title={language === "vi" ? "Thêm vào giỏ" : "カートに追加"}>
            <ShoppingFilled className='icon-filled' onClick={handleAddToCart} />
          </Tooltip>
        </div>
      </div>
      <p className='product-category text-xs text-[#353535] opacity-70 mt-2'>
        {product?.category?.name}
      </p>
      <p className='product-name group-hover:text-pink-500 cursor-pointer'>
        {product?.name}
      </p>
      <p className='product-price py-4'>
        <b
          className={
            product?.salePrice ? "text-sm text-gray-500 line-through" : ""
          }
        >
          {parseInt(product?.price).toLocaleString("vi", {
            style: "currency",
            currency: "VND",
          })}
        </b>
        {product?.salePrice && (
          <b className='ml-2'>
            {parseInt(product?.salePrice).toLocaleString("vi", {
              style: "currency",
              currency: "VND",
            })}
          </b>
        )}
      </p>
    </div>
  );
}

export default ProductCard;
